<?php	$pdo = new PDO("mysql:dbname=jigsaw;host=localhost", "root", "");	$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);		function save_contact($object){		global $pdo;				$pdo_statement = $pdo->prepare("			UPDATE				contacts 			SET				name = :name,				title = :title,				email = :email,				phone = :phone,				company_name = :company_name,				company_address = :company_address,				company_address2 = :company_address2,				company_city = :company_city,				company_zip = :company_zip,				company_country = :company_country, 				saved = 1			WHERE				id = :id		");		foreach($object as $key => $value){			$pdo_statement->bindValue($key, $value);		}		$pdo_statement->execute();	}		if(isset($_POST['object'])){		$object = $_POST['object'];		if(get_magic_quotes_gpc()){			$object = stripslashes($object);		}		$object_nonfiltered = $object;		$object = "";		for($i = 0; $i < strlen($object_nonfiltered); $i++){			$char = substr($object_nonfiltered, $i, 1);			if(ord($char) <= 128){				$object .= $char;			}		}		save_contact(json_decode($object));	}?>OK